const fs = require('fs');
const path = require('path');
const decompress = require('decompress-zip');
const unzipper = new decompress(path.join(__dirname, '../build/wallaby.bin'));

unzipper.on('extract', function (log) {
  fs.unlinkSync(path.join(__dirname, '../build/wallaby.bin'));
});

unzipper.extract({
  path: path.join(__dirname, '../build')
});